function ContactItem({
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  href,
  hidden = true,
  type,
  title,
  subtitle,
  icon,
}: // onClick,
{
  href?: string;
  hidden: boolean;
  type?: "phone" | "email" | "website" | "chatbot" | "contact";
  icon: ReactNode;
  title: string | ReactNode;
  subtitle: string;
  // onClick?: () => void;
}) {
  const handleAttr = () => {
    if (type === "email") {
      return { href: `mailto:${title}` };
    }
    if (type === "phone") {
      return { href: `tel:${title}` };
    }
    if (type === "website") {
      return { href: title as string, target: "_blank" };
    }
    if (type === "chatbot" || type === "contact") {
      // return { onClick };
    }
    return {};
  };

  if (hidden) {
    return null;
  }
  return (
    <a
      className={clsx(
        "flex items-center py-3 text-black transition-colors",
        (type === "chatbot" || type === "contact") && "cursor-pointer",
        type ? "hover:text-[#6f44fc]" : "hover:text-black"
      )}
      {...handleAttr()}
    >
      <div className="w-14 h-14 shrink-0 rounded-full bg-slate-200 flex justify-center items-center mr-4">
        {icon}
      </div>
      <div className="flex-1">
        <p
          className={clsx(
            "font-semibold",
            ["website", "email"].includes(type as string)
              ? "break-all"
              : "break-words",
            typeof title === "string" && title.length > 50
              ? "text-sm"
              : "text-base"
          )}
        >
          {title}
        </p>
        <p className="text-sm text-slate-400">{subtitle}</p>
      </div>
    </a>
  );
}

